export default (sequelize) => {
  const db = {};
  const context = require.context(
    ".",
    true,
    /^\.\/(?!index\.js).*\.js$/,
    "sync"
  );
  context
    .keys()
    .map(context)
    .forEach((module) => {
      const model = module(sequelize, Sequelize);
      db[model.name] = model;
